document.addEventListener("DOMContentLoaded",()=>{function n(){document.querySelectorAll('[data-element="number"]').forEach(t=>{try{const e=t.textContent.trim(),r=parseFloat(e);if(isNaN(r))throw new Error(`Invalid number: ${e}`);const o=t.getAttribute("data-locale")||"en-US",a=new Intl.NumberFormat(o).format(r);t.textContent=a}catch{}})}n()});
